import React, { useState, useEffect } from 'react';
import "../styles/speakers.css"

// =========================================================================
// !!! БЛОК ДИНАМІЧНОГО ІМПОРТУ ДЛЯ WEBPACK (CREATE REACT APP) !!!
// =========================================================================

// 1. Створюємо контекст для папки './img/'
// Аргументи:
// - '../img/' : Шлях до папки (відносно поточного файлу Speakers.jsx, який знаходиться в src/components/)
// - false : Не шукати у вкладених папках
// - /\.jpg$/ : Шукати файли, що закінчуються на .jpg (якщо є інші формати, додайте їх: /(\.jpg|\.png)$/)
const req = require.context('../img/', false, /\.jpg$/);

// 2. Створюємо словник { "ім'я_файлу.jpg": "урл_з_хешем" }
const speakerImages = req.keys().reduce((acc, path) => {
    // path буде виглядати як './bohdan_didenko.jpg'
    const fileName = path.split('/').pop(); // Отримуємо 'bohdan_didenko.jpg'

    // require(path) повертає об'єкт модуля з властивістю 'default'
    acc[fileName] = req(path).default;
    return acc;
}, {});
// =========================================================================


const Speakers = () => {

    // Об'єднаний список усіх учасників для відображення з фото
    const allParticipants = [
        // === ОСНОВНІ СПІКЕРИ ===
        { name: "Богдан Діденко", title: "Спікер 1 (час 11:15-11:55)", type: "main", imgName: "bohdan_didenko.jpg" },
        { name: "Сивоконь Олексій", title: "Спікер 2 (час 12:00-12:40)", type: "main", imgName: "syvokon_oleksiy.jpg" },
        { name: "Бойчук Андрій", title: "Спікер 3 (час 15:20-16:00)", type: "main", imgName: "boychuk_andriy.jpg" },

        // === ПАНЕЛЬНА ДИСКУСІЯ 1: Інвестори ===
        { name: "Кицмей Тарас", title: "Панель 1: Інвестори / Панель 3: Етика", type: "panel", imgName: "kytsmey_taras.jpg" },
        { name: "Горовий Євген", title: "Панель 1: Інвестори", type: "panel", imgName: "horovyi_yevhen.jpg" },
        { name: "Панін Юрій (Lapa)", title: "Панель 1: Інвестори", type: "panel", imgName: "panin_yuriy.jpg" },
        { name: "Костянтин Комаров (Uklon)", title: "Панель 1: Інвестори", type: "panel", imgName: "komarov_kostyantyn.jpg" },

        // === ПАНЕЛЬНА ДИСКУСІЯ 2: Технічна тема ===
        { name: "Яцишин Володя", title: "Модератор Панелі 2: AI-Engineering Panel", type: "panel", imgName: "yatsyshyn_volodymyr.jpg" },
        { name: "Засоба Олександр", title: "Панель 2: AI-Engineering Panel", type: "panel", imgName: "zasoba_oleksandr.jpg" },
        { name: "Шамуратов Олексій", title: "Панель 2: AI-Engineering Panel", type: "panel", imgName: "shamuratov_oleksiy.jpg" },
        { name: "Чирка Юрій", title: "Панель 2: AI-Engineering Panel", type: "panel", imgName: "chyrka_yuriy.jpg" },

        // === ПАНЕЛЬНА ДИСКУСІЯ 3: Етика та Соціальні аспекти ===
        { name: "Молчановський Олексій", title: "Панель 3: Етичні та соціальні аспекти", type: "panel", imgName: "molchanovsky_oleksiy.jpg" },
        { name: "Москаленко Андрій", title: "Панель 3: Етичні та соціальні аспекти", type: "panel", imgName: "moskalenko_andriy.jpg" },

        // === ІНШІ УЧАСНИКИ ===
        { name: "Шаховська Наталя", title: "Вітальне слово / Учасник Панелі 3", type: "other", imgName: "shakhovska_natalia.jpg" },
        { name: "Мельникова Наталя", title: "Вітальне слово / Модератор Панелі 3 / Нагородження", type: "other", imgName: "melnykova_natalia.jpg" },
        { name: "Басистюк Олег", title: "Звіт по БФК та Аукціон", type: "other", imgName: "basystyuk_oleh.jpg" },
    ];

    // Розділяємо список на секції
    const mainSpeakers = allParticipants.filter(p => p.type === 'main');
    const panelParticipants = allParticipants.filter(p => p.type === 'panel');
    const otherParticipants = allParticipants.filter(p => p.type === 'other');


    // Функція-компонент для відображення зображення з обробкою помилок
    const SpeakerImage = ({ imgName, name }) => {
        const [isImageBroken, setIsImageBroken] = useState(false);

        // !!! ВИКОРИСТАННЯ ІМПОРТОВАНОГО URL !!!
        const imageUrl = speakerImages[imgName];

        // Додаткова перевірка, якщо файл не знайдено в мапі (помилка в назві файлу)
        useEffect(() => {
            if (!imageUrl) {
                console.error(`ERROR: Файл зображення "${imgName}" не знайдено в мапі імпортів.`);
                setIsImageBroken(true);
            }
        }, [imageUrl, imgName]);


        const handleError = (e) => {
            // Ця функція спрацює, якщо навіть імпортований URL не завантажиться (наприклад, мережева помилка)
            console.error(`ERROR: Помилка завантаження зображення для: ${name} (Очікуваний шлях: ${imageUrl})`);
            setIsImageBroken(true);
            e.target.style.display = 'none'; // Приховуємо поламаний тег <img>
        };

        // Відображаємо <img> або текстовий плейсхолдер
        return (
            <div className="speakers__img">
                {imageUrl && !isImageBroken ? (
                    <img
                        src={imageUrl} // Використовуємо URL, який згенерував бандлер
                        alt={name}
                        className="speaker__photo"
                        onError={handleError}
                        style={{ width: '100%', height: '100%', objectFit: 'cover' }}
                    />
                ) : (
                    <div className="speaker__placeholder" style={{
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        textAlign: 'center',
                        backgroundColor: '#7929e330', // Напівпрозорий фіолетовий плейсхолдер
                        color: '#FFF',
                        fontSize: '14px',
                        padding: '10px',
                        height: '100%',
                        width: '100%'
                    }}>
                        Фото не знайдено для {name}
                    </div>
                )}
            </div>
        );
    };


    // Функція для рендерингу блоку учасника (без змін)
    const renderParticipantBlock = (person, index, offset = 0) => {
        // Встановлюємо індекс для чергування: парний -> img-txt, непарний -> txt-img
        const effectiveIndex = index + offset;
        const isImageFirst = effectiveIndex % 2 === 0;

        // Стилі для коректного відображення <img> замість фону в CSS
        const photoStyle = {
            width: '300px', // Ширина, як у speakers__img в CSS
            height: '300px', // Висота, як у speakers__img в CSS
        };

        if (isImageFirst) {
            return (
                <div className="speakers__content__img" key={person.name}>
                    <div className="speakers__img" style={photoStyle}>
                        <SpeakerImage imgName={person.imgName} name={person.name} />
                    </div>
                    <div className="speakers__txt">
                        <h3>{person.name}</h3>
                        <h4>{person.title}</h4>
                    </div>
                </div>
            );
        } else {
            return (
                <div className="speakers__content__txt" key={person.name}>
                    <div className="speakers__txt__end">
                        <h3>{person.name}</h3>
                        <h4>{person.title}</h4>
                    </div>
                    <div className="speakers__img" style={photoStyle}>
                        <SpeakerImage imgName={person.imgName} name={person.name} />
                    </div>
                    <div className="speakers__txt__adaptive">
                        <h3>{person.name}</h3>
                        <h4>{person.title}</h4>
                    </div>
                </div>
            );
        }
    };


    return (
        <section className='speakers snap-section' id='speakers'>

            {/* ======================================================= */}
            {/* 1. ОСНОВНІ СПІКЕРИ */}
            {/* ======================================================= */}
            <div className="speakers__title__container">
                <h1 className="title__speakers">Основні спікери</h1>
            </div>
            <div className="speakers__container">
                {mainSpeakers.map((person, index) => renderParticipantBlock(person, index))}
            </div>

            <hr style={{width: '80%', margin: '40px auto', borderTop: '1px solid rgba(255, 255, 255, 0.1)'}} />

            {/* ======================================================= */}
            {/* 2. ПАНЕЛЬНІ ДИСКУСІЇ */}
            {/* ======================================================= */}
            <div className="speakers__title__container">
                <h1 className="title__speakers">Учасники панельних дискусій</h1>
            </div>
            <div className="speakers__container">
                {/* Для забезпечення коректного чергування, використовуємо відступ offset */}
                {panelParticipants.map((person, index) =>
                    renderParticipantBlock(person, index, mainSpeakers.length)
                )}
            </div>

            <hr style={{width: '80%', margin: '40px auto', borderTop: '1px solid rgba(255, 255, 255, 0.1)'}} />

            {/* ======================================================= */}
            {/* 3. ІНШІ УЧАСНИКИ */}
            {/* ======================================================= */}
            <div className="speakers__title__container">
                <h1 className="title__speakers">Інші учасники програми</h1>
            </div>
            <div className="speakers__container" style={{paddingBottom: '50px'}}>
                {/* Відступ для продовження чергування */}
                {otherParticipants.map((person, index) =>
                    renderParticipantBlock(person, index, mainSpeakers.length + panelParticipants.length)
                )}
            </div>
        </section>
    )
}

export default Speakers
